// Simple ethers.js shim for this project
(function(global) {
    console.log('Loading simplified ethers.js shim...');
    
    // Create a minimal ethers object
    const ethers = {};
    
    // Add basic utilities
    ethers.utils = {
        parseEther: function(ethValue) {
            // 1 ETH = 10^18 Wei
            return BigInt(Math.floor(parseFloat(ethValue) * 1e18)).toString();
        },
        formatEther: function(weiValue) {
            return (parseInt(weiValue) / 1e18).toString();
        }
    };
    
    // Expose ethers globally
    global.ethers = ethers;
    
    console.log('Ethers.js shim loaded successfully!');
})(typeof globalThis !== 'undefined' ? globalThis : window);
